// src/SchedulerApp.js
import React, {
  useState,
  useEffect,
  forwardRef,
  useImperativeHandle,
  useMemo,
} from 'react';
import './App.css';

// --- Time and Date Utilities ---
const TIME_SLOTS = [];
for (let i = 7; i < 22; i++) {
  TIME_SLOTS.push(`${String(i).padStart(2, '0')}:00`);
  TIME_SLOTS.push(`${String(i).padStart(2, '0')}:30`);
}

function getWeekDates(start) {
  const week = [];
  const d = new Date(start);
  d.setMinutes(d.getMinutes() + d.getTimezoneOffset());
  d.setDate(d.getDate() - d.getDay());
  for (let i = 0; i < 7; i++) {
    week.push(new Date(d));
    d.setDate(d.getDate() + 1);
  }
  return week;
}

// --- Placeholder Scheduling Logic ---
// This is the core function we will build upon.
// For now, it just assigns a default task during work hours.
function generateDailySchedule(employees, date) {
  const schedule = new Map();
  const dayOfWeek = date.getDay();

  // Return empty schedule for weekends
  if (dayOfWeek === 0 || dayOfWeek === 6) {
    return schedule;
  }

  employees.forEach(emp => {
    const dailyTasks = new Map();
    TIME_SLOTS.forEach(slot => {
        // Simple logic: If within shift hours, assign specialist task. Otherwise, 'OFF'.
        // We will replace this with real scheduling logic.
        const [hour] = slot.split(':').map(Number);
        const startHour = parseInt(emp.shift.start.split(':')[0], 10);
        const endHour = parseInt(emp.shift.end.split(':')[0], 10);

        if(hour >= startHour && hour < endHour) {
            dailyTasks.set(slot, emp.specialistTask);
        } else {
            dailyTasks.set(slot, 'OFF');
        }
    });
    schedule.set(emp.name, dailyTasks);
  });

  return schedule;
}


const SchedulerApp = forwardRef(function SchedulerApp(
  { initialEmployees = [], weekStart },
  ref
) {
  const [employees, setEmployees] = useState(initialEmployees);
  const weekDates = useMemo(() => getWeekDates(weekStart || new Date()), [weekStart]);

  // Generate the full weekly schedule based on the current employees and week
  const weeklySchedule = useMemo(() => {
    const fullSchedule = new Map();
    weekDates.forEach(date => {
        if (date.getDay() >= 1 && date.getDay() <= 5) { // Only for Mon-Fri
            fullSchedule.set(date.toISOString().split('T')[0], generateDailySchedule(employees, date));
        }
    });
    return fullSchedule;
  }, [employees, weekDates]);

  useImperativeHandle(ref, () => ({
    // ... (Your update and clear functions will go here later)
  }));

  const weekdays = weekDates.filter(d => d.getDay() >= 1 && d.getDay() <= 5);

  return (
    <div className="scheduler-container">
      {weekdays.map(date => {
        const dateStr = date.toISOString().split('T')[0];
        const dailySchedule = weeklySchedule.get(dateStr);
        if (!dailySchedule) return null;

        return (
          <div key={dateStr} className="day-schedule">
            <h3>{date.toLocaleDateString('en-US', { weekday: 'long', month: 'short', day: 'numeric' })}</h3>
            <table className="schedule-table">
              <thead>
                <tr>
                  <th>Employee</th>
                  {TIME_SLOTS.map(slot => <th key={slot} className="time-slot-header">{slot}</th>)}
                </tr>
              </thead>
              <tbody>
                {employees.map(emp => (
                  <tr key={emp.name}>
                    <td>{emp.name}</td>
                    {TIME_SLOTS.map(slot => {
                      const task = dailySchedule.get(emp.name)?.get(slot) || 'OFF';
                      return (
                        <td key={slot} className={`task-cell task-${task.toLowerCase().replace(/[^a-z]/g, '')}`}>
                          {task !== 'OFF' ? task : ''}
                        </td>
                      );
                    })}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )
      })}
    </div>
  );
});

export default SchedulerApp;
