import React, { useState } from 'react';

function App() {
  const [selectedTime, setSelectedTime] = useState(null);
  const [isConfirmed, setIsConfirmed] = useState(false);

  // Sample available time slots
  const timeSlots = [
    '9:00 AM', '9:30 AM', '10:00 AM', '10:30 AM',
    '11:00 AM', '11:30 AM', '1:00 PM', '1:30 PM',
    '2:00 PM', '2:30 PM', '3:00 PM', '3:30 PM',
  ];

  const handleTimeSelect = (time) => {
    setSelectedTime(time);
    setIsConfirmed(false); // Reset confirmation if a new time is chosen
  };

  const handleConfirm = () => {
    if (selectedTime) {
      setIsConfirmed(true);
    }
  };

  return (
    <div className="container mx-auto p-8 max-w-2xl">
      <div className="bg-white rounded-lg shadow-lg p-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-2">Book a Meeting</h1>
        <p className="text-gray-600 mb-6">Please select a time slot below.</p>

        {isConfirmed ? (
          <div className="text-center p-8 bg-green-100 border-l-4 border-green-500 rounded-md">
            <h2 className="text-2xl font-semibold text-green-800">Appointment Confirmed!</h2>
            <p className="mt-2 text-lg text-green-700">Your meeting is set for <span className="font-bold">{selectedTime}</span>.</p>
          </div>
        ) : (
          <>
            <div className="grid grid-cols-3 sm:grid-cols-4 gap-4">
              {timeSlots.map((time) => (
                <button
                  key={time}
                  onClick={() => handleTimeSelect(time)}
                  className={`p-3 rounded-lg text-center font-semibold transition-all duration-200
                    ${selectedTime === time 
                      ? 'bg-blue-600 text-white shadow-md' 
                      : 'bg-gray-200 text-gray-700 hover:bg-blue-200'
                    }`}
                >
                  {time}
                </button>
              ))}
            </div>

            {selectedTime && (
              <div className="mt-8 text-center">
                <p className="text-lg mb-4">
                  You have selected: <span className="font-bold text-blue-600">{selectedTime}</span>
                </p>
                <button
                  onClick={handleConfirm}
                  className="bg-green-500 hover:bg-green-600 text-white font-bold py-3 px-8 rounded-lg shadow-lg transition-transform transform hover:scale-105"
                >
                  Confirm Appointment
                </button>
              </div>
            )}
          </>
        )}
      </div>
       <footer className="text-center mt-6 text-gray-500 text-sm">
        <p>Scheduling Assistant Demo</p>
      </footer>
    </div>
  );
}

export default App;
